import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit {
  loginForm: FormGroup;
  hide: boolean = false;
  constructor() {

    this.loginForm = new FormGroup({
      email: new FormControl('', [Validators.required, Validators.email]),
      password: new FormControl('', [Validators.required, Validators.minLength(6)])
    });
  }

  ngOnInit() {
    // Other initialization code
  }

  onLogin() {
    if (this.loginForm.valid) {
      const email = this.loginForm.value.email;
      const password = this.loginForm.value.password;
      // Implement your login logic here
    }
  }
}
<p>
    <mat-toolbar>
      <p><span>STL </span>Technologies</p>
      <button mat-raised-button class="btn-raise">Basic</button>
    </mat-toolbar>
  </p>

<mat-card>
    <mat-card-content>
        <div class="header">
            <h2>Welcome Back! Please login to proceed</h2>
        </div>
        <form (ngSubmit)="onLogin()" name="loginForm" [formGroup]="loginForm">
            <div class="email-input">
                <mat-form-field class="full-width" appearance="outline">
                    <mat-label>Email</mat-label>
                    <input formControlName = "email" matInput required>
                    <mat-error *ngIf="loginForm.get('email')?.errors?.['required']">Required Email Adress</mat-error>
                </mat-form-field>
            </div>

            <div class="password-input">
                <mat-form-field class="full-width" appearance="outline">
                    <mat-label>Password</mat-label>
                    <input formControlName="password" matInput [type]="hide ? 'password' : 'text'" required >
                    <button mat-icon-button matSuffix (click)="hide = !hide" [attr.aria-label] = "'Hide Password'" [attr.aria-pressed] = "'Hide'">
                        <mat-icon>{{ hide ? 'visibility_off' : 'visibility'}}</mat-icon>
                    </button>
                    <mat-error *ngIf="loginForm.get('password')?.errors?.['required']">Required Password</mat-error>
                </mat-form-field>
            </div>
            <button mat-flat-button color="primary" class="blink">Login</button>
            <button mat-flat-button color="primary" class="blink">Forgot Password</button>
        </form>
    </mat-card-content>
</mat-card>
